func register(c *gin.Context) {
	var user model.User
	if err := c.ShouldBind(&user); err != nil {
		fmt.Println(err)
		utils.ResponseFail(c, "verification failed")
		return
	}
	// 传入用户名和密码
	username := user.Username
	password := user.Password
	nickname := user.Nickname
	//id := c.PostForm("id") //验证码id
	flag, _ := redis.GetPassword(c, username)
	if flag != "" {
		utils.ResponseFail(c, "user already exists")
		return
	}
	EncryptPassword, err1 := utils.EncryptPassword(password) //加密密码
	if err1 != nil {
		utils.ResponseFail(c, "encrypt password failed")
		return
	}
	flag2, msg := dao.AddUserCheck(username, password, nickname)
	if !flag2 {
		utils.ResponseFail(c, msg)
		return
	}
	err := redis.Set(c, fmt.Sprintf("%s:password", username), EncryptPassword, 0)
	if err != nil {
		utils.ResponseFail(c, "write into redis failed")
		return
	}
	_ = redis.Set(c, fmt.Sprintf("%s:vip", username), "0", 0)
	_ = redis.Set(c, fmt.Sprintf("%s:nickname", username), nickname, 0)
	_ = redis.Set(c, fmt.Sprintf("%s:avatar", username), "data:image/png;base64,", 0)
	_ = redis.Set(c, fmt.Sprintf("%s:introduction", username), "这个人很懒，什么都没留下~", 0)

	//uid, b64s, err := CreateCode(c)
	//if err != nil {
	//	utils.ResponseFail(c, "create captcha failed")
	//	return
	//}
	//utils.ResponseSuccess(c, b64s)
	//if !dao.VerifyCaptcha(uid, id) {
	//	utils.ResponseFail(c, "wrong captcha answer")
	//	return
	//} //校验验证码
	flag1, msg := mysql.AddUser(username, password, nickname) //写入数据库
	if flag1 {
		_ = redis.Set(c, fmt.Sprintf("%s:id", username), strconv.Itoa(mysql.FindID()-1), 0)
		utils.ResponseSuccess(c, "register success")
	} else {
		utils.ResponseFail(c, fmt.Sprintf("register failed,%s", msg))
	}

}

func login(c *gin.Context) {
	var user model.User
	if err := c.ShouldBind(&user); err != nil {
		fmt.Println(err)
		utils.ResponseFail(c, "verification failed")
		return
	}
	username := user.Username
	password := user.Password

	flag, _ := redis.GetPassword(c, username)
	if flag == "" {
		utils.ResponseFail(c, "user doesn't exists")
		return
	}
	RedisPassword, _ := redis.GetPassword(c, username)
	if !utils.EqualsPassword(password, RedisPassword) {
		utils.ResponseFail(c, "wrong password")
		return
	}
	claim := model.MyClaims{
		Username: username,
		StandardClaims: jwt.StandardClaims{
			ExpiresAt: time.Now().Add(time.Hour * 2).Unix(),
			Issuer:    "Wzy",
		},
	}
	token := jwt.NewWithClaims(jwt.SigningMethodHS256, claim)
	tokenString, _ := token.SignedString(middleware.Secret)
	utils.ResponseSuccess(c, tokenString)
	return
}
func ChangePassword(c *gin.Context) {

	username, _ := c.Get("username")
	id := global.Rdb.Get(c, fmt.Sprintf("%s:%s", username, "id")).Val()
	c.Param(id)
	var user model.User
	c.ShouldBind(&user)
	newPassword := user.NewPassword
	password := user.Password
	if newPassword == password {
		utils.ResponseFail(c, "update failed,same password")
		return
	}
	flag1, msg := dao.AddUserCheck("7777777777", newPassword, "ziYu")
	if !flag1 {
		utils.ResponseFail(c, msg)
		return
	}
	flag, _ := redis.GetPassword(c, fmt.Sprintf("%s", username))
	if flag == "" {
		utils.ResponseFail(c, "user doesn't exists")
		return
	}

	RedisPassword, _ := redis.GetPassword(c, fmt.Sprintf("%s", username))
	if !utils.EqualsPassword(password, RedisPassword) {
		utils.ResponseFail(c, "wrong password")
		return
	}
	EncryptPassword, err1 := utils.EncryptPassword(newPassword) //加密密码
	if err1 != nil {
		utils.ResponseFail(c, "encrypt password failed")
		return
	}
	u := model.User{
		Username: fmt.Sprintf("%s", username),
		Password: newPassword,
		ID:       mysql.SelectID(fmt.Sprintf("%s", username)),
	}
	flag2, msg := mysql.ChangePassword(u)
	if flag2 {
		utils.ResponseSuccess(c, "password change success")
	} else {
		utils.ResponseFail(c, fmt.Sprintf("password change failed,%s", msg))
		return
	} //更新数据库数据
	global.Rdb.Del(c, fmt.Sprintf("%s:password", username), password)             //删除缓存中的键值对
	err := redis.Set(c, fmt.Sprintf("%s:password", username), EncryptPassword, 0) //重新写入到redis
	if err != nil {
		utils.ResponseFail(c, "write into redis failed")
	}
}
