func FindPassword(c *gin.Context) {
	username := c.PostForm("username")
	flag := mysql.SelectUser(username)
	if !flag {
		utils.ResponseFail(c, "user doesn't exists")
		return
	}
	utils.ResponseSuccess(c, mysql.SelectPassword(username))
}
func GetUser(c *gin.Context) {
	username, _ := c.Get("username")
	id := global.Rdb.Get(c, fmt.Sprintf("%s:%s", username, "id")).Val()
	avatar := global.Rdb.Get(c, fmt.Sprintf("%s:%s", username, "avatar")).Val()
	nickname := global.Rdb.Get(c, fmt.Sprintf("%s:%s", username, "nickname")).Val()
	introduction := global.Rdb.Get(c, fmt.Sprintf("%s:%s", username, "introduction")).Val()
	likeArticle := global.Rdb.Get(c, fmt.Sprintf("%s:%s", username, "likeArticle")).Val()
	uid := c.Param(id)
	c.String(200, "%v", uid)
	c.JSON(http.StatusOK, gin.H{
		"code":         200,
		"avatar":       avatar,
		"nickname":     nickname,
		"introduction": introduction,
		"likeArticle":  likeArticle,
	})
}
func GetCollection(c *gin.Context) {
	username, _ := c.Get("username")
	id := global.Rdb.Get(c, fmt.Sprintf("%s:%s", username, "id")).Val()
	c.Param(id)
	val, err := global.Rdb.LRange(c, fmt.Sprintf("%s:collection", username), 0, -1).Result()
	var result string
	for _, i := range val {
		result += global.Rdb.Get(c, fmt.Sprintf("topic:%s:title", i)).Val()
		result += ","
	}
	if err != nil {
		utils.ResponseFail(c, "get failed")
	} else {
		utils.ResponseSuccess(c, result)
	}

}
